<template>
  <div id="stars-background">
    <div id="stars"></div>
    <div id="stars2"></div>
    <div id="stars3"></div>
    <div class="back">
        <div class="user-card">
            <img style="margin:15px 10px" :src="profile" width="264" height="64">
            <a class="edit" style="cursor:pointer; color:#6C63FF" @click="disabled = false">Editar</a>
            <div class="input-group mb-3">
                <span class="input-group-text" id="basic-addon1">Email:</span>
                <input class="form-control" type="email" v-model="email" :disabled="disabled" :class="{'disabled': disabled}">
            </div>
            <div class="input-group mb-3">
                <span class="input-group-text" id="basic-addon2">Nome:</span>
                <input class="form-control" type="text" v-model="name" :disabled="disabled" :class="{'disabled': disabled}">
            </div>
            <small style="margin-bottom:10px;color:white"> Criado:
                <small style="margin-bottom:10px;color:#6C63FF"> {{ created_at || 'null' }}</small>
            </small>
            <small style="margin-bottom:10px;color:white"> Atualizado:
                <small style="margin-bottom:10px;color:#6C63FF"> {{ updated_at || 'null' }}</small>
            </small>
            <button  style="width:100%" class="btn btn-dark" @click="editUser()" :disabled="disabled">SALVAR</button>

        </div>
    </div>
  </div>
</template>

<script>
import api from '../../api.js';
export default {

    data() {
        return {
            profile: require('@/assets/icons/profile.svg'),
            id: null,
            email: null,
            name: null,
            created_at: null,
            updated_at: null,
            disabled: true
        }
    },
    methods: {
        async loggedUser() {
            try {
                const user = await api.get('api/loggedUser');
                this.email = user.data.data.email;
                this.name = user.data.data.name;
                this.created_at = user.data.data.created_at;
                this.updated_at = user.data.data.updated_at;

                const meses = ["Jan", "Fev", "Mar", "Abr", "Mai", "Jun", "Jul","Ago","Set","Out","Nov","Dez"];
                let created_at = new Date(this.created_at);
                let updated_at = new Date(this.updated_at);
                this.created_at = ((created_at.getDate() + " " + meses[(created_at.getMonth())] + " " + created_at.getFullYear()));
                this.updated_at = ((updated_at.getDate() + " " + meses[(updated_at.getMonth())] + " " + updated_at.getFullYear()));
                
                this.id = user.data.data.id;
                
            } catch (error) {
                this.$toast.open({ message: 'Erro ao encontrar usuário', type: 'error' })
                console.error(error)
            }
        },
        async editUser() {
            try {
              const user = await api.put(`api/users/${this.id}`, {email: this.email, name: this.name});
              localStorage.setItem('user', JSON.stringify(user.data.data));
              this.disabled = true;
              window.dispatchEvent(new CustomEvent('updateName', {
                detail: {
                  name: user.data.data.name
                }
              }));
              this.$toast.open({ message: 'Usuário atualizado com sucesso!', type: 'success' })
            } catch (error) {
              this.$toast.open({ message: 'Erro ao salvar usuário', type: 'error' })
              console.error(error)
            }
        },
    },
    mounted() {
        this.loggedUser();
    }
}
</script>

